import React, { useState } from 'react'

export default function TextForm(props) {
    const handleUpClick = () => {
        // console.log("Uppercase has clicked");
        let newText = text.toUpperCase();
        setText(newText);
    }
    const handleLoClick = () => {
        // console.log("Uppercase has clicked");
        let newText = text.toLowerCase();
        setText(newText);
    }
    const handleOnChange = (event) => {
        // console.log("handleOnChange has clicked");
        setText(event.target.value);
    }
    const [text, setText] = useState("Enter text hear...");
    return (
        <>
            <div className = "container">
                <h1>{props.heading}</h1>
                <div className="mb-3">
                    {/* <label for="myBox" className="form-label">Example textarea</label> */}
                    <textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" rows="8"></textarea>
                </div>
                <button className="btn btn-primary" onClick={handleUpClick}>Convert to uppercase</button>
                <button className="btn btn-primary" onClick={handleLoClick}>Convert to lowercase</button>
                <button className="btn btn-primary" onClick={CLEAAR}>Convert to lowercase</button>
            </div>
            <div className = "container my-2">
                <h1>Your text summery</h1>
                <p>{text.split(" ").length} words and {text.length} character</p>
                <p>{0.008*60*text.split(" ").length} seconds required to read the text</p>
                <h2>Preview</h2>
                <p>{text}</p>
            </div>
        </>
    )
}
